import React, { useState, useEffect } from 'react';
import axios from 'axios'
import './doctorProfile.css'
import { getUserFromStorage } from '../../../../../utils/storage'

function DoctorProfile() {
  const user = getUserFromStorage()
    const cin = user.cin_personnel
    const [responseStatus, setResponseStatus] = useState(0);
    const [responseMessage, setResponseMessage] = useState('');
    const [showPassword, setShowPassword] = useState(false);

    const [formData, setFormData] = useState({
        cin: '',
        name: '',
        date: '',
        tel: '',
        email: '',
        password: ''
    });

    // Toggles the showPassword state
    const togglePasswordVisibility = () => {
        setShowPassword(!showPassword);
    };

    // state of inputs
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const validateEmail = (email) => {
        return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
    };

    useEffect(() => {
        async function fetchData() {
            try {
                const response = await axios.get('http://127.0.0.1:5000/profile', {
                    params: { cin: cin },
                    headers: {
                        'Content-Type': 'application/json'
                    }
                });

                const data = response.data;
                console.log(data);
                setResponseMessage(data.message);

                if (response.status === 201) {
                    setFormData({
                        cin: data.cin_personnel,
                        name: data.full_name,
                        date: new Date(data.date_of_birth).toISOString().slice(0, 10),
                        tel: data.phone_number,
                        email: data.email,
                        password: data.password
                    });
                    setResponseStatus(response.status);
                    console.log("Success:", data);
                } else {
                    console.error("Failure:", data);
                }
            } catch (error) {
                console.error("Error during the request:", error.message);
            }
        }

        fetchData();
    }, [cin]);

  return (
    <div>
      <h2 id='profile2'>Profile</h2>

      <form className="doctorProfile">
        <input
          type="text"
          name="cin"
          placeholder="CIN"
          required
        />
        <input
          type="text"
          name="name"
          placeholder="Full Name"
          required
        />
        <label htmlFor="date">Date of birth:
          <input
            type="date"
            name="date"
            required
          />
        </label>
        <input
          type="tel"
          name="tel"
          placeholder="Phone number"
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          required
        />
        <button type="submit">Save</button>
      </form>
    </div>
  )
}

export default DoctorProfile
