import React, { useState } from 'react'
import './patients.css'

function Patients() {

  const [formData, setFormData] = useState({});

  const [responseMessage, setResponseMessage] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value, // Dynamically setting the name or email based on the input that's changed.
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    console.log(formData)

    const response = await fetch('/add_person', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    }).then(data);

    const data = await response.json();
    setResponseMessage(data.message);

    if (response.status === 201) {
      // Handle success
      console.log("Success:", data);
    } else {
      // Handle failure
      console.error("Failure:", data);
    }
  };

  return (
    <div>
      <h2 id='patients'>Patients</h2>

      <form className="form1">
        <h4>Find a patient:</h4>
        <div className="row">
          <input type="text" name="cin" placeholder="CIN" required />
          <button type="submit">Search</button>
        </div>
      </form>

      <form className="form1" onSubmit={handleSubmit}>
        <h4>Add a patient:</h4>
        <div className="row">
          <input type="text" name="cin" placeholder="CIN" required onChange={handleInputChange} />
          <input type="text" name="name" placeholder="Full Name" required onChange={handleInputChange} />
        </div>
        <div className="row">
          <label htmlFor="date">Date of birth:
            <input type="date" name="date" required onChange={handleInputChange} />
          </label>
          <label htmlFor="sex">Sex:
            <select name="sex" id="sex" onChange={handleInputChange}>
              <option value="male">Male</option>
              <option value="female">Female</option>
            </select>
          </label>
        </div>
        <div className="row">
          <input type="tel" name="tel" placeholder="Phone number" required onChange={handleInputChange} />
          <input type="email" name="email" placeholder="Email" required onChange={handleInputChange} />
        </div>
        <input type="text" name="adress" placeholder="Adress" required onChange={handleInputChange} />
        <button type="submit">Add</button>
      </form>
      {responseMessage && <p style={{ color: 'black' }}>{responseMessage}</p>}
    </div>
  )
}

export default Patients
