import React, { useState, useEffect } from 'react';
import axios from 'axios'
import './nurseProfile.css'
import { getUserFromStorage } from '../../../../../utils/storage';

function NurseProfile() {
    const user = getUserFromStorage()
    const [responseStatus, setResponseStatus] = useState(0);
    const [responseMessage, setResponseMessage] = useState('');

    const [formData, setFormData] = useState({
        cin: '',
        name: '',
        date: '',
        tel: '',
        email: '',
        password: ''
    });

    // state of inputs
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const validateEmail = (email) => {
        return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
    };

    useEffect(async () => {
        try {
            const response = await axios.get('http://127.0.0.1:5000/profile', {
                params: { cin: user.cin_personnel, type: user },
                headers: {
                    'Content-Type': 'application/json'
                }
            });

            // Extract data from the response
            const data = response.data;
            setResponseMessage(data.message);  // Assuming this is a function you've defined to set a response message

            if (response.status === 201) {
                // Handle success
                setFormData({
                    cin: data.cin_personnel,
                    name: data.full_name,
                    date: new Date(data.date_of_birth).toISOString().slice(0, 10),
                    tel: data.phone_number,
                    email: data.email,
                    password: data.password
                });
                setResponseStatus(response.status)
                console.log("Success:", data);
            } else {
                // Handle failure
                console.error("Failure:", data);
            }

        } catch (error) {
            // Handle errors from the request itself (e.g., network errors, timeout errors)
            console.error("Error during the request:", error.message);
        }
    }, []);



    return (
        <div>
            <h2 id='profile1'>Profile</h2>

            <form className="nurseProfile">
                <input
                    type="text"
                    name="cin"
                    placeholder="CIN"
                    value={formData.cin}
                    required
                    onChange={handleInputChange}
                />
                <input
                    type="text"
                    name="name"
                    placeholder="Full Name"
                    value={formData.name}
                    required
                    onChange={handleInputChange}
                />
                <label htmlFor="date">Date of birth:
                    <input
                        type="date"
                        name="date"
                        value={formData.date}
                        required
                        onChange={handleInputChange}
                    />
                </label>
                <input
                    type="tel"
                    name="tel"
                    placeholder="Phone number"
                    value={formData.tel}
                    required
                    onChange={handleInputChange}
                />
                <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    value={formData.email}
                    required
                    onChange={handleInputChange}
                />
                <input
                    type="password"
                    name="password"
                    placeholder="Password"
                    value={formData.password}
                    required
                    onChange={handleInputChange}
                />
                <button type="submit">Save</button>
            </form>
            {responseMessage && responseStatus === 201 ? <p style={{ color: 'green', marginLeft: '30px' }}>{responseMessage}</p> : <p style={{ color: 'red', marginLeft: '30px' }}>{responseMessage}</p>}
        </div>
    )
}

export default NurseProfile
